import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const time = new Date().getHours();
let greetings = '';
let heading = '';
if (time < 12) {
   heading = {
    color: "#fa9191",
    margin: "200px 0",
  };

   greetings = 'Good Morning';

} else if (time < 12 && time > 21) {
   heading = {
    color: "#fa9191",
    margin: "200px 0",
  };
  greetings = 'Good Afternoon';
} else {
   heading = {
    color: "black",
    margin: "200px 0",
  };
  greetings = 'Good Evening';
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <>
    <div className="box">
      <h1 className="heading" style={heading}>
        Hello Sir, <span className="colorGreet"> {greetings} </span> 
      </h1>
    </div>
  </>
);
