import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

const time = new Date().getHours();

if(time > 12){

  const heading = {
    color: '#fa9191',
     margin: '200px 0',
}
}else if(time < 12 && time > 21)  {

  const heading = {
    color: '#fa9191',
     margin: '200px 0',
}

}else{
  const heading = {
    color: 'black',
     margin: '200px 0',
}

}





const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <>

      <div className='box'>
        <h1 className='heading' style={heading}>Hello Sir, {time} Good Morning</h1>
      </div>

    </>
);
